mapper.select:
select num, owner_id, created_at, updated_at, state, topic, html_url, content
from issue_helper
where num = #{num,jdbcType=INTEGER}

select html_url
from issue_helper
where num = #{num,jdbcType=INTEGER}

select num, owner_id, created_at, updated_at, state, topic, html_url, content
from issue_helper
where sort = #{word,jdbcType=VARCHAR}

select num, owner_id, created_at, updated_at, state, topic, html_url, content, sort
from issue_helper
where position(#{word,jdbcType=VARCHAR} in topic) > 0
  and left(created_at,10)>=#{time1,jdbcType=VARCHAR}
  and #{time2,jdbcType=VARCHAR}>=left(created_at,10)
  and sort=#{sort,jdbcType=VARCHAR}
