mapper.insert:
insert into time_compare (id, created_at, updated_at, 
  sorts, topic, state
  )
values (#{id,jdbcType=INTEGER}, #{createdAt,jdbcType=VARCHAR}, #{updatedAt,jdbcType=VARCHAR}, 
  #{sorts,jdbcType=VARCHAR}, #{topic,jdbcType=VARCHAR}, #{state,jdbcType=VARCHAR}
  )

mapper.select:
select distinct sorts,count(sorts)over (partition by sorts) as num from time_compare
where  #{time2,jdbcType=VARCHAR}>created_at and created_at>#{time1,jdbcType=VARCHAR}
order by sorts;
